import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const referralCode = localStorage.getItem("referralCode");
  const [donations, setDonations] = useState([]);
  const [total, setTotal] = useState(0);
  const [originalAmounts, setOriginalAmounts] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!referralCode) return;

    axios.get(`http://localhost:5000/api/donations/user/${referralCode}`)
      .then(res => {
        setDonations(res.data.donations || []);
        setTotal(res.data.totalRaised || 0);
        setOriginalAmounts(res.data.originalAmounts || "");
      })
      .catch(() => alert("⚠️ Failed to load dashboard"))
      .finally(() => setLoading(false));
  }, [referralCode]);

  const handleLogout = () => {
    localStorage.removeItem("referralCode");
    window.location.href = "/login";
  };

  return (
    <div style={{
      minHeight: "100vh",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      background: "#f0f2f5",
      fontFamily: "Arial, sans-serif"
    }}>
      <div style={{
        width: "90%",
        maxWidth: "800px",
        background: "#fff",
        padding: "30px",
        borderRadius: "12px",
        boxShadow: "0 4px 15px rgba(0,0,0,0.1)"
      }}>
        <h2 style={{ textAlign: "center" }}>🎉 Welcome Intern!</h2>
        <p style={{ textAlign: "center" }}>Your Referral Code: <b>{referralCode}</b></p>
        <p style={{ textAlign: "center", fontSize: "18px" }}>
          Total Raised: <b>₹{total} {originalAmounts && `(${originalAmounts})`}</b>
        </p>

        {/* Buttons Section */}
        <div style={{ textAlign: "center", marginTop: "15px" }}>
          <a href={`/donate/${referralCode}`}>
            <button style={btnStyle}>🔗 Share Donation Link</button>
          </a>
          <a href="/leaderboard">
            <button style={{ ...btnStyle, backgroundColor: "#28a745" }}>🏆 View Leaderboard</button>
          </a>
          <button onClick={handleLogout} style={{ ...btnStyle, backgroundColor: "#dc3545" }}>🚪 Logout</button>
        </div>

        {/* Transaction History */}
        <h3 style={{ marginTop: "30px", textAlign: "center" }}>📜 Transaction History</h3>
        {loading ? (
          <p style={{ textAlign: "center" }}>Loading transactions...</p>
        ) : donations.length > 0 ? (
          <table style={{
            width: "100%",
            borderCollapse: "collapse",
            marginTop: "10px",
            fontSize: "16px"
          }}>
            <thead>
              <tr style={{ background: "#007bff", color: "#fff" }}>
                <th style={thTdStyle}>Donor Name</th>
                <th style={thTdStyle}>Amount</th>
                <th style={thTdStyle}>Date</th>
              </tr>
            </thead>
            <tbody>
              {donations.map((d, i) => (
                <tr key={i} style={{ background: i % 2 === 0 ? "#f9f9f9" : "#fff" }}>
                  <td style={thTdStyle}>{d.name || "Anonymous"}</td>
                  <td style={thTdStyle}>{d.currencySymbol}{d.amount}</td>
                  <td style={thTdStyle}>{new Date(d.date).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p style={{ textAlign: "center" }}>No donations yet.</p>
        )}
      </div>
    </div>
  );
}

/* ✅ Styles */
const btnStyle = {
  margin: "5px",
  padding: "10px 15px",
  backgroundColor: "#007bff",
  color: "#fff",
  border: "none",
  borderRadius: "6px",
  cursor: "pointer",
  fontSize: "15px"
};

const thTdStyle = {
  border: "1px solid #ddd",
  padding: "8px",
  textAlign: "center"
};
