import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const referralCode = localStorage.getItem("referralCode");
  const [donations, setDonations] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!referralCode) {
      setError("Referral code missing. Please login again.");
      return;
    }

    axios
      .get(`http://localhost:5000/api/donations/user/${referralCode}`)
      .then((res) => {
        setDonations(res.data.donations || []);
        setTotal(res.data.totalRaised || 0);
      })
      .catch(() => setError("Failed to load your transactions."))
      .finally(() => setLoading(false));
  }, [referralCode]);

  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (loading) return <p>Loading your dashboard...</p>;

  return (
    <div className="dashboard">
      <h2>Welcome Intern!</h2>
      <p>Your Referral Code: <b>{referralCode}</b></p>
      <p>Total Raised: ₹{total}</p>

      <a href={`/donate/${referralCode}`}><button>Share Donation Link</button></a>
      <a href="/leaderboard"><button>View Leaderboard</button></a>

      <h3>Transactions</h3>
      {donations.length === 0 ? (
        <p>No donations yet.</p>
      ) : (
        <ul>
          {donations.map((d, i) => (
            <li key={i}>
              {d.name || "Anonymous"} donated ₹{d.amount} on{" "}
              {new Date(d.date).toLocaleDateString()}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
