import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const referralCode = localStorage.getItem("referralCode");
  const [donations, setDonations] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!referralCode) {
      alert("‚ö†Ô∏è Referral Code Missing! Please log in again.");
      return;
    }

    axios
      .get(`http://localhost:5000/api/donations/user/${referralCode}`)
      .then((res) => {
        setDonations(res.data.donations || []);
        setTotal(res.data.totalRaised || 0);
      })
      .catch(() => {
        alert("‚ö†Ô∏è Failed to load dashboard data");
      })
      .finally(() => setLoading(false));
  }, [referralCode]);

  return (
    <div className="dashboard" style={{ padding: "20px" }}>
      <h2>üéâ Welcome Intern!</h2>
      <p>Your Referral Code: <b>{referralCode}</b></p>

      {/* ‚úÖ Total Raised Always in INR */}
      <p>Total Raised: <b>‚Çπ{Math.round(total)}</b></p>

      <div style={{ marginTop: "10px" }}>
        <a href={`/donate/${referralCode}`}><button>üîó Share Donation Link</button></a>
        <a href="/leaderboard" style={{ marginLeft: "10px" }}><button>üèÜ View Leaderboard</button></a>
      </div>

      <h3 style={{ marginTop: "20px" }}>üìú Transaction History</h3>

      {loading ? (
        <p>Loading transactions...</p>
      ) : donations.length > 0 ? (
        <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th>Donor Name</th>
              <th>Amount</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {donations.map((d, i) => (
              <tr key={i}>
                <td>{d.name || "Anonymous"}</td>

                {/* ‚úÖ Show correct currency symbol + code */}
                <td>{`${d.currencySymbol}${d.amount} (${d.currency})`}</td>

                <td>{new Date(d.date).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No donations yet.</p>
      )}
    </div>
  );
}
