import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Leaderboard() {
  const [leaders, setLeaders] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/donations/leaderboard")
      .then(res => setLeaders(res.data))
      .catch(() => alert("Failed to load leaderboard"));
  }, []);

  return (
    <div>
      <h2>üèÜ Leaderboard</h2>
      <table border="1">
        <thead>
          <tr>
            <th>Rank</th>
            <th>User</th>
            <th>Total Raised</th>
          </tr>
        </thead>
        <tbody>
           {leaders.length > 0 ? leaders.map((u, i) => (
              <tr key={i}>
      <td>{i + 1}</td>
      <td>{u.userName} ({u.referralCode})</td>
      <td>‚Çπ{u.totalRaised}</td>
    </tr>
  )) : (
    <tr><td colSpan="3">No donations yet</td></tr>
  )}
</tbody>

      </table>
    </div>
  );
}
