import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const [donations, setDonations] = useState([]);
  const [totalRaised, setTotalRaised] = useState(0);
  const [loading, setLoading] = useState(true);

  const referralCode = localStorage.getItem("referralCode"); // ✅ Ensure this is saved on login

  useEffect(() => {
    if (!referralCode) {
      alert("⚠️ Referral Code Missing! Please log in again.");
      return;
    }

    axios
      .get(`http://localhost:5000/api/donations/user/${referralCode}`)
      .then((res) => {
        setDonations(res.data.donations || []);
        setTotalRaised(res.data.totalRaised || 0);
      })
      .catch(() => alert("⚠️ Failed to load dashboard"))
      .finally(() => setLoading(false));
  }, [referralCode]);

  return (
    <div style={{ padding: "20px" }}>
      <h2>📊 My Dashboard</h2>

      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          {/* ✅ Total Raised in INR */}
          <h3>
            Total Raised: <span style={{ color: "#00c8ff" }}>₹{Math.round(totalRaised)}</span>
          </h3>

          {/* ✅ Donation History Table */}
          {donations.length > 0 ? (
            <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
              <thead>
                <tr>
                  <th>Donor Name</th>
                  <th>Amount</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>
                {donations.map((d, i) => (
                  <tr key={i}>
                    <td>{d.name}</td>

                    {/* ✅ Show original currency with correct symbol */}
                    <td>
                      {d.currencySymbol}
                      {d.amount} ({d.currency})
                    </td>

                    <td>{new Date(d.date).toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No donations yet.</p>
          )}
        </>
      )}
    </div>
  );
}
