import React, { useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

export default function DonatePage() {
  const { referralCode } = useParams();
  const navigate = useNavigate();
  const [donorName, setDonorName] = useState("");
  const [amount, setAmount] = useState("");

  const handleDonate = async () => {
    if (!donorName || !amount) {
      alert("Please enter name and amount");
      return;
    }

    // ‚úÖ Confirmation popup
    const confirmDonation = window.confirm(`Are you sure you want to donate ‚Çπ${amount}?`);
    if (!confirmDonation) return;

    try {
      await axios.post(`http://localhost:5000/api/donations/${referralCode}`, { donorName, amount });
      alert("‚úÖ Donation successful!");
      navigate("/dashboard"); // ‚úÖ Redirect back to dashboard
    } catch (err) {
      alert("‚ùå Donation failed. Try again.");
    }
  };

  return (
    <div className="donate-page">
      <h2>üíñ Support with a Donation</h2>
      <p>Referral Code: <b>{referralCode}</b></p>
      <input 
        type="text" 
        placeholder="Your Name" 
        value={donorName} 
        onChange={e => setDonorName(e.target.value)} 
      />
      <input 
        type="number" 
        placeholder="Amount (‚Çπ)" 
        value={amount} 
        onChange={e => setAmount(e.target.value)} 
      />
      <button onClick={handleDonate}>Donate</button>
    </div>
  );
}
