import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const [userData, setUserData] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  const referralCode = localStorage.getItem("referralCode");
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      window.location.href = "/login";
      return;
    }

    // ‚úÖ Fetch User Data
    axios.get(`http://localhost:5000/api/donations/user/${referralCode}`)
      .then(res => setUserData(res.data))
      .catch(() => alert("‚ö†Ô∏è Failed to load dashboard"));

    // ‚úÖ Fetch Transactions
    axios.get(`http://localhost:5000/api/donations/transactions/${referralCode}`)
      .then(res => setTransactions(res.data || []))
      .catch(() => alert("‚ö†Ô∏è Failed to load transactions"))
      .finally(() => setLoading(false));
  }, [referralCode, token]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("referralCode");
    window.location.href = "/login";
  };

  if (loading) return <p>Loading...</p>;

  // ‚úÖ Build Original Currency Summary with Symbols (e.g., $100 + ‚Ç¨50)
  const currencySymbols = { INR: "‚Çπ", USD: "$", EUR: "‚Ç¨", GBP: "¬£", JPY: "¬•" };
  let currencySummary = {};
  (userData?.donations || []).forEach(d => {
    if (!currencySummary[d.currency]) currencySummary[d.currency] = 0;
    currencySummary[d.currency] += d.amount;
  });

  const originalCurrencyDisplay = Object.entries(currencySummary)
    .map(([curr, amt]) => `${currencySymbols[curr] || curr}${amt}`)
    .join(" + ");

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>üéâ Welcome Intern!</h2>
      <p><strong>Your Referral Code:</strong> {referralCode}</p>

      {/* ‚úÖ Total Raised */}
      <p>
        <strong>Total Raised:</strong> ‚Çπ{Math.round(userData?.totalRaised || 0)}
        {originalCurrencyDisplay && ` (${originalCurrencyDisplay})`}
      </p>

      {/* ‚úÖ Action Links */}
      <div style={{ marginBottom: "10px" }}>
        <a href={`/donate/${referralCode}`} style={{ marginRight: "10px" }}>üîó Share Donation Link</a>
        <a href="/leaderboard" style={{ marginRight: "10px" }}>üèÜ View Leaderboard</a>
        <button
          onClick={handleLogout}
          style={{ background: "red", color: "white", border: "none", padding: "5px 10px", cursor: "pointer" }}
        >
          üö™ Logout
        </button>
      </div>

      {/* ‚úÖ Transaction History */}
      <h3>üìú Transaction History</h3>
      {transactions.length > 0 ? (
        <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th>Donor Name</th>
              <th>Amount</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((t, i) => (
              <tr key={i}>
                <td>{t.name}</td>
                <td>{t.currencySymbol}{t.amount} ({t.currency})</td>
                <td>{new Date(t.date).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No transactions yet.</p>
      )}
    </div>
  );
}
