import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const [transactions, setTransactions] = useState([]);
  const [totalRaised, setTotalRaised] = useState(0);
  const [referralCode, setReferralCode] = useState("");
  const [userName, setUserName] = useState("");

  useEffect(() => {
    const storedReferral = localStorage.getItem("referralCode");
    const storedName = localStorage.getItem("userName");
    if (storedReferral) setReferralCode(storedReferral);
    if (storedName) setUserName(storedName);

    if (storedReferral) {
      axios.get(`http://localhost:5000/api/donations/user/${storedReferral}`)
        .then(res => {
          setTransactions(res.data.donations || []);
          setTotalRaised(res.data.totalRaised || 0);
        })
        .catch(() => alert("‚ö†Ô∏è Failed to load dashboard"));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("referralCode");
    localStorage.removeItem("userName");
    localStorage.removeItem("token");
    window.location.href = "/login";
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>üéâ Welcome Intern!</h2>
      <p><strong>Your Referral Code:</strong> {referralCode}</p>
      <p><strong>Total Raised:</strong> ‚Çπ{Math.round(totalRaised)}</p>

      <button onClick={() => window.location.href = `/donate/${referralCode}`}>üîó Share Donation Link</button>
      <button onClick={() => window.location.href = "/leaderboard"}>üèÜ View Leaderboard</button>
      <button onClick={handleLogout}>üö™ Logout</button>

      <h3>üìú Transaction History</h3>
      {transactions.length === 0 ? (
        <p>No transactions yet.</p>
      ) : (
        <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th>Donor Name</th>
              <th>Amount</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((t, i) => (
              <tr key={i}>
                <td>{t.name}</td>
                <td>{t.currencySymbol}{t.amount}</td>
                <td>{new Date(t.date).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
