import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const referralCode = localStorage.getItem("referralCode");
  const [donations, setDonations] = useState([]);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/donations/${referralCode}`)
      .then(res => {
        setDonations(res.data.donations);
        setTotal(res.data.totalRaised);
      });
  }, [referralCode]);

  return (
    <div className="dashboard">
      <h2>Welcome Intern!</h2>
      <p>Your Referral Code: <b>{referralCode}</b></p>
      <p>Total Raised: ₹{total}</p>
      <a href={`/donate/${referralCode}`}><button>Share Donation Link</button></a>
      <a href="/leaderboard"><button>View Leaderboard</button></a>
      <h3>Transactions</h3>
      <ul>
        {donations.map((d, i) => (
          <li key={i}>{d.name} donated ₹{d.amount}</li>
        ))}
      </ul>
    </div>
  );
}
