import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const [userData, setUserData] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  const referralCode = localStorage.getItem("referralCode");
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      window.location.href = "/login";
      return;
    }

    axios.get(`http://localhost:5000/api/donations/user/${referralCode}`)
      .then(res => setUserData(res.data))
      .catch(() => alert("⚠️ Failed to load dashboard"));

    axios.get(`http://localhost:5000/api/donations/transactions/${referralCode}`)
      .then(res => setTransactions(res.data || []))
      .catch(() => alert("⚠️ Failed to load transactions"))
      .finally(() => setLoading(false));
  }, [referralCode, token]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("referralCode");
    window.location.href = "/login";
  };

  if (loading) return <p>Loading...</p>;

  // ✅ Build Original Currency Summary
  const currencySymbols = { INR: "₹", USD: "$", EUR: "€", GBP: "£", JPY: "¥" };
  let currencySummary = {};
  (userData?.donations || []).forEach(d => {
    if (!currencySummary[d.currency]) currencySummary[d.currency] = 0;
    currencySummary[d.currency] += d.amount;
  });

  const originalCurrencyDisplay = Object.entries(currencySummary)
    .map(([curr, amt]) => `${currencySymbols[curr] || curr}${amt}`)
    .join(" + ");

  return (
    <div style={{ fontFamily: "Arial", background: "#f4f6f8", minHeight: "100vh", padding: "30px" }}>
      {/* ✅ Dashboard Box */}
      <div style={{
        maxWidth: "500px",
        margin: "0 auto",
        background: "#fff",
        padding: "20px",
        borderRadius: "10px",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
        textAlign: "center"
      }}>
        <h2>🎉 Welcome Intern!</h2>
        <p><strong>Your Referral Code:</strong> {referralCode}</p>

        {/* ✅ Total Raised */}
        <h3 style={{ margin: "15px 0", color: "#2c3e50" }}>
          Total Raised: ₹{Math.round(userData?.totalRaised || 0)} 
          {originalCurrencyDisplay && ` (${originalCurrencyDisplay})`}
        </h3>

        {/* ✅ Buttons */}
        <div style={{ display: "flex", justifyContent: "center", gap: "10px", marginTop: "10px" }}>
          <a href={`/donate/${referralCode}`}>
            <button style={btnStyle}>🔗 Share Donation Link</button>
          </a>
          <a href="/leaderboard">
            <button style={btnStyle}>🏆 View Leaderboard</button>
          </a>
          <button onClick={handleLogout} style={{ ...btnStyle, background: "#e74c3c" }}>🚪 Logout</button>
        </div>
      </div>

      {/* ✅ Transaction History */}
      <div style={{
        maxWidth: "700px",
        margin: "30px auto",
        background: "#fff",
        padding: "20px",
        borderRadius: "10px",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
      }}>
        <h3>📜 Transaction History</h3>
        {transactions.length > 0 ? (
          <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead style={{ background: "#2c3e50", color: "#fff" }}>
              <tr>
                <th>Donor Name</th>
                <th>Amount</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((t, i) => (
                <tr key={i}>
                  <td>{t.name}</td>
                  <td>{t.currencySymbol}{t.amount} ({t.currency})</td>
                  <td>{new Date(t.date).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No transactions yet.</p>
        )}
      </div>
    </div>
  );
}

// ✅ Button Style
const btnStyle = {
  background: "#3498db",
  color: "white",
  border: "none",
  padding: "8px 12px",
  borderRadius: "5px",
  cursor: "pointer",
  fontWeight: "bold"
};
