import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const referralCode = localStorage.getItem("referralCode");
  const [donations, setDonations] = useState([]);
  const [total, setTotal] = useState(0);
  const [originalAmounts, setOriginalAmounts] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!referralCode) {
      window.location.href = "/login";
      return;
    }

    axios.get(`http://localhost:5000/api/donations/user/${referralCode}`)
      .then(res => {
        setDonations(res.data.donations || []);
        setTotal(res.data.totalRaised || 0);
        setOriginalAmounts(res.data.originalAmounts || "");
      })
      .catch(() => alert("‚ö†Ô∏è Failed to load dashboard"))
      .finally(() => setLoading(false));
  }, [referralCode]);

  const handleLogout = () => {
    localStorage.removeItem("referralCode");
    window.location.href = "/login";
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      {/* ‚úÖ Dashboard Card */}
      <div style={{
        maxWidth: "600px",
        margin: "0 auto",
        background: "white",
        padding: "20px",
        borderRadius: "10px",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
      }}>
        <h2>üéâ Welcome Intern!</h2>
        <p>Your Referral Code: <b>{referralCode}</b></p>

        {/* ‚úÖ Total Raised */}
        <p style={{ fontSize: "18px", fontWeight: "bold" }}>
          Total Raised: ‚Çπ{Math.round(total)} {originalAmounts && `(${originalAmounts})`}
        </p>

        {/* ‚úÖ Action Buttons */}
        <div style={{ marginTop: "10px" }}>
          <a href={`/donate/${referralCode}`}><button style={btn}>üîó Share Donation Link</button></a>
          <a href="/leaderboard" style={{ marginLeft: "10px" }}><button style={btn}>üèÜ View Leaderboard</button></a>
          <button onClick={handleLogout} style={{ ...btn, background: "red", marginLeft: "10px" }}>üö™ Logout</button>
        </div>
      </div>

      {/* ‚úÖ Transaction History */}
      <div style={{
        maxWidth: "700px",
        margin: "20px auto",
        background: "white",
        padding: "20px",
        borderRadius: "10px",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
      }}>
        <h3>üìú Transaction History</h3>
        {loading ? <p>Loading transactions...</p> : (
          donations.length > 0 ? (
            <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
              <thead style={{ background: "#2c3e50", color: "#fff" }}>
                <tr>
                  <th>Donor Name</th>
                  <th>Amount</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>
                {donations.map((d, i) => (
                  <tr key={i}>
                    <td>{d.name || "Anonymous"}</td>
                    <td>{d.currencySymbol}{d.amount} ({d.currency})</td>
                    <td>{new Date(d.date).toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No donations yet.</p>
          )
        )}
      </div>
    </div>
  );
}

// ‚úÖ Button Style
const btn = {
  background: "#3498db",
  color: "white",
  border: "none",
  padding: "8px 12px",
  borderRadius: "5px",
  cursor: "pointer",
  fontWeight: "bold"
};
