import React, { useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

export default function DonationPage() {
  const { referralCode } = useParams();
  const [donorName, setDonorName] = useState("");
  const [amount, setAmount] = useState("");

  const handleDonate = async () => {
    try {
      await axios.post(`http://localhost:5000/api/donations/${referralCode}`, { donorName, amount });
      alert("Thank you for your donation!");
    } catch (err) {
      alert("Donation failed");
    }
  };

  return (
    <div className="donate-box">
      <h2>Donate to Support</h2>
      <input placeholder="Your Name" onChange={(e) => setDonorName(e.target.value)} /><br />
      <input placeholder="Amount" type="number" onChange={(e) => setAmount(e.target.value)} /><br />
      <button onClick={handleDonate}>Donate</button>
    </div>
  );
}
