import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const [userData, setUserData] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  const referralCode = localStorage.getItem("referralCode");
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      window.location.href = "/login";
      return;
    }

    // ✅ Fetch User Data
    axios.get(`http://localhost:5000/api/donations/user/${referralCode}`)
      .then(res => setUserData(res.data))
      .catch(() => alert("⚠️ Failed to load dashboard"));

    // ✅ Fetch Transactions
    axios.get(`http://localhost:5000/api/donations/transactions/${referralCode}`)
      .then(res => setTransactions(res.data || []))
      .catch(() => alert("⚠️ Failed to load transactions"))
      .finally(() => setLoading(false));
  }, [referralCode, token]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("referralCode");
    window.location.href = "/login";
  };

  if (loading) return <p>Loading...</p>;

  // ✅ Build Original Currency Summary
  let currencySummary = {};
  (userData?.donations || []).forEach(d => {
    if (!currencySummary[d.currency]) currencySummary[d.currency] = 0;
    currencySummary[d.currency] += d.amount;
  });
  const originalCurrencyDisplay = Object.entries(currencySummary)
    .map(([curr, amt]) => `(${d.currencySymbol || ''}${amt} ${curr})`)
    .join(" + ");

  return (
    <div style={{ padding: "20px", maxWidth: "700px", margin: "auto", fontFamily: "Arial" }}>
      <h2>🎉 Welcome Intern!</h2>
      <p><strong>Your Referral Code:</strong> {referralCode}</p>

      {/* ✅ Total Raised */}
      <h3>
        Total Raised: 
        <span style={{ color: "green" }}> ₹{Math.round(userData?.totalRaised || 0)} </span>
        {originalCurrencyDisplay && <span> ({originalCurrencyDisplay})</span>}
      </h3>

      {/* ✅ Links */}
      <div style={{ marginBottom: "15px" }}>
        <a href={`/donate/${referralCode}`} style={{ marginRight: "10px" }}>🔗 Share Donation Link</a>
        <a href="/leaderboard" style={{ marginRight: "10px" }}>🏆 View Leaderboard</a>
        <button onClick={handleLogout} style={{ background: "red", color: "white", border: "none", padding: "5px 10px", cursor: "pointer" }}>🚪 Logout</button>
      </div>

      {/* ✅ Transaction History */}
      <h3>📜 Transaction History</h3>
      {transactions.length > 0 ? (
        <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th>Donor Name</th>
              <th>Amount</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((t, i) => (
              <tr key={i}>
                <td>{t.name}</td>
                <td>{t.currencySymbol}{t.amount} ({t.currency})</td>
                <td>{new Date(t.date).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No transactions yet.</p>
      )}
    </div>
  );
}
